import _objectSpread from "@babel/runtime-corejs2/helpers/esm/objectSpread";
// EntityNew        - The expected creation shape from the user
// EntityShapeToDB  - The doc as it exists in Firebase
// EntityShape      - The doc with the id as a property

/**
 * Meetings
 */
export function meetingFromSnap(snap) {
  var data = snap.data();
  return _objectSpread({
    id: snap.id,
    title: '',
    focus: null,
    participantIDs: {},
    observerIDs: {},
    participantData: {}
  }, data, {
    // @ts-ignore
    createdAt: data.createdAt || {
      seconds: 0,
      nanoseconds: 0
    }
  });
}
/**
 * Tickets
 */

export function ticketFromSnap(snap) {
  var data = snap.data() || {};
  return _objectSpread({
    id: snap.id,
    title: '',
    link: '',
    description: '',
    votes: {},
    isRevealed: false,
    source: 'form'
  }, data, {
    // @ts-ignore
    createdAt: data.createdAt || {
      seconds: 0,
      nanoseconds: 0
    }
  });
}
/**
 * Credentials
 */

export function profileFromSnap(snap) {
  var data = snap.data();
  return _objectSpread({
    hasCreds: false
  }, data);
}
